    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }
}

contracts/IPrecogMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

/**
 * @title IPrecogMarket: simple interface to interact with all Precog Markets
 * @author Marto (https://github.com/0xMarto)
 * @dev Feel free to make any adjustments to the code (DMs are open @0xMarto)
 */
interface IPrecogMarket {
    // Public market variables
    function owner() external view returns (address);

    function token() external view returns (address);

    function id() external view returns (uint256);

    function totalOutcomes() external view returns (uint256);

    function oracle() external view returns (address);

    function startTimestamp() external view returns (uint256);

    function endTimestamp() external view returns (uint256);

    function closeTimestamp() external view returns (uint256);

    function result() external view returns (uint256);

    function accountShares(address _account) external view returns (
        uint256 buys, uint256 sells, uint256 deposited, uint256 withdrew, uint256 redeemed
    );

    // Events emitted by markets
    event SharesBought(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenIn);
    event SharesSold(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenOut);
    event SharesRedeemed(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenOut);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @notice Initializes the contract with the specified token
     * @param _token The address of the ERC-20 token to be used for buy, sell, and redeem shares
     */
    function initialize(address _token) external;

    /**
     * @notice Sets up the market with the specified parameters
     * @param _id The unique identifier for the market
     * @param _oracle The address of the oracle that will report results
     * @param _totalOutcomes The number of possible outcomes for the market
     * @param _subsidy The initial funding used to seed the market (defined on initialized token)
     * @param _overround The AMM profit margin in basis points (bps) [recommended: (100 * _totalOutcomes)]
     */
    function setup(uint256 _id, address _oracle, uint256 _totalOutcomes, uint256 _subsidy, uint256 _overround) external;

    /**
     * @notice Buys outcome shares for the specified outcome
     * @param _outcome The outcome of which shares are being bought (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome tokens to buy (as a signed 64.64-bit fixed point number)
     * @return tokenCost The total token amount used for buying the specified amount of outcome shares
     */
    function buy(uint256 _outcome, int128 _amount) external returns (uint256 tokenCost);

    /**
     * @notice Allows buying market shares for a specified account (limited to only Self or Owner)
     * @param _outcome The outcome of which shares are being bought (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to buy (as a signed 64.64-bit fixed point number)
     * @param _account The address of the buyer
     * @return tokenCost The total token amount used for buying the specified amount of outcome shares
     */
    function _buy(uint256 _outcome, int128 _amount, address _account) external returns (uint256 tokenCost);

    /**
     * @notice Sells outcome shares for the specified outcome
     * @param _outcome The outcome of which shares are being sold (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to sell (as a signed 64.64-bit fixed point number)
     * @return tokenReturn The total amount of tokens received from selling the outcome shares
     */
    function sell(uint256 _outcome, int128 _amount) external returns (uint256 tokenReturn);

    /**
     * @notice Allows selling market shares from a specified account (limited to only Self or Owner)
     * @param _outcome The outcome for which tokens are being sold (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome tokens to sell (as a signed 64.64-bit fixed point number)
     * @param _account The address of the seller
     * @return tokenReturn The total amount of tokens received from selling the outcome shares
     */
    function _sell(uint256 _outcome, int128 _amount, address _account) external returns (uint256 tokenReturn);

    /**
     * @notice Reports the result of the market (limited to only Oracle)
     * @param _id The unique identifier of the market
     * @param _outcome The outcome that is reported as the result of the market
     */
    function reportResult(uint256 _id, uint256 _outcome) external;

    /**
     * @notice Redeems the current sender shares for the result of the market
     * @return redeemedShares The number of shares redeemed
     */
    function redeemShares() external returns (uint256 redeemedShares);

    /**
     * @notice Redeems the received account shares for the result of the market (limited to only Self or Owner)
     * @param _account The address of the account with shares of the market
     * @return redeemedShares The number of shares redeemed
     */
    function _redeem(address _account) external returns (uint256 redeemedShares);

    /**
     * @notice Redeems shares in batch for multiple accounts  (limited to only Oracle)
     * @param _accounts The list of accounts to redeem shares for
     * @dev The list of accounts could be calculated using the "SharesBought" event
     * @return redeems The number of successful redeems
     */
    function redeemBatch(address[] memory _accounts) external returns (uint256 redeems);

    /**
     * @notice Withdraws any remaining liquidity from the market (limited to only owner)
     * @param _token The address of the ERC-20 token to withdraw
     */
    function withdraw(address _token) external;

    /**
     * @notice Transfers the ownership of the contract to a new owner (limited to only current owner)
     * @param _newOwner The address of the new owner
     */
    function transferOwnership(address _newOwner) external;

    /**
     * @notice Update the oracle address of the market (limited to only owner)
     * @param _newOracle The address of the EOA or contract that can close the market
     */
    function updateOracle(address _newOracle) external;

    /**
     * @notice Updates the start and end timestamps for the market (limited to only owner)
     * @param _startTimestamp The timestamp when the market starts allowing trading
     * @param _endTimestamp The timestamp when the market stops allowing trading
     */
    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) external;

    /**
     * @notice Gets the total collateral spent in the market
     * @return totalCost The total cost in the form of a signed 64.64-bit fixed point number
     */
    function cost() external view returns (int128 totalCost);

    /**
     * @notice Gets the cost of buying the specified amount of outcome tokens
     * @param _outcome The outcome for which tokens are being bought
     * @param _amount The number of outcome tokens to buy (as signed 64.64-bit fixed point number)
     * @return tokenCost The token cost amount (as a signed 64.64-bit fixed point number)
     */
    function buyPrice(uint256 _outcome, int128 _amount) external view returns (int128 tokenCost);

    /**
     * @notice Gets the return from selling the specified amount of outcome tokens
     * @param _outcome The outcome for which tokens are being sold
     * @param _amount The number of outcome tokens to sell (as signed 64.64-bit fixed point number)
     * @return tokenReturn The token return amount (as a signed 64.64-bit fixed point number)
     */
    function sellPrice(uint256 _outcome, int128 _amount) external view returns (int128 tokenReturn);

    /**
     * @notice Gets the current market state information
     * @return totalShares The current total shares minted for all outcomes of the market
     * @return sharesBalances All shares balances (indexed by outcome)
     * @return currentCost The current liquidity of the market
     * @return totalBuys Buys counter of the market
     * @return totalSells Sells counter of the market
     */
    function getMarketInfo() external view returns (
        int128 totalShares, int128[] memory sharesBalances, int128 currentCost, uint256 totalBuys, uint256 totalSells
    );

    /**
     * @notice Get current market buy and sell prices for all outcomes
     * @dev Helper function to fast calculate market prediction and spreads
     * @return buyPrices buy price of 1 share for all outcomes (indexed by outcome)
     * @return sellPrices sell price of 1 share for all outcomes (indexed by outcome)
     */
    function getPrices() external view returns (uint256[] memory buyPrices, uint256[] memory sellPrices);

    /**
     * @notice Gets the amount of shares that an account owns for all outcomes
     * @param _account The address of the account with shares of the market
     * @return balances The balances of shares for all outcomes (indexed by outcome)
     */
    function getAccountOutcomeBalances(address _account) external view returns (uint256[] memory balances);
}

contracts/IPrecogToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title IPrecogToken: interface to interact with Precog Token
 */
interface IPrecogToken is IERC20 {
    /**
     * @notice Mint new tokens for the specified address (limited to only owner)
     * @param to The address of the new tokens receiver
     * @param amount The amount of tokens to be minted
     * @dev Emits a {Transfer} event from the zero address as source
     */
    function mint(address to, uint256 amount) external;

    /**
     * @notice Burn already minted tokens from the specified address (limited to only owner)
     * @param from The address of the token source
     * @param amount The amount of tokens to be burn
     * @dev Emits a {Transfer} event to the zero address as destination
     */
    function burn(address from, uint256 amount) external;

    /**
     * @notice Transfer tokens from one address to another (limited to only owner)
     * @param from The address of the token source
     * @param to The address of the token receiver
     * @dev Emits a {Transfer} event
     */
    function move(address from, address to, uint256 amount) external;

    /**
     * @notice Transfer ownership of the contract to a new address (limited to only owner)
     * @param newOwner The address of the new owner
     * @dev Emits a {OwnershipTransferred} event with the previous and new owner addresses
     */
    function transferOwnership(address newOwner) external;

    /**
     * @notice Get the current owner of the contract
     * @return Address of the current owner
     */
    function owner() external view returns (address);

    /**
     * @notice Emitted when ownership of the contract is transferred to a new address
     */
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
}

contracts/PrecogMarketV6.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import {ABDKMath} from "./ABDKMath64x64.sol";

/**
 * @title PrecogMasterV6: An implementation for liquidity-sensitive LMSR market maker in Solidity
 * @author Marto (built based on Abdulla Al-Kamil implementation)
 * @dev Feel free to make any adjustments to the code
 */
contract PrecogMarketV6 {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct SharesInfo {
        uint256 buys;  // Counter on Buy
        uint256 sells;  // Counter on Sell
        uint256 deposited; // Collateral-In on Buys
        uint256 withdrew; // Collateral-Out on Sells
        uint256 redeemed; // Collateral-Out on Redeems
        mapping(uint256 => uint256) balances;  // Shares balances (by outcome) [has an custom getter]
    }

    // Public market variables
    address public owner;  // Should be the PrecogMaster contract
    address public token;  // Collateral to Buy and redeem shares
    uint256 public id;  // Unique Identifier for the market
    address public oracle;  // EOA or Contract in change to reportResults
    uint256 public startTimestamp;  // Time when Buy/Sell shares are enabled
    uint256 public endTimestamp;  // Time when Buy/Sell shares are disabled
    uint256 public closeTimestamp;  // Time when results were reported
    uint256 public result;  // Final outcome of the market (published by the oracle)
    mapping(address => SharesInfo) public accountShares;  // Account shares balances and info

    // Private market variables
    // Note: This contract uses the ABDKMath library to allow for math functions such as logarithms and exponents.
    //       All the sensitive state variables are stored as int128 (signed 64.64 bit fixed point number).
    uint256 private totalOutcomes;
    int128[] private shares;
    int128 private beta;
    int128 private alpha;
    int128 private dust;
    int128 private currentCost;
    int128 private totalShares;
    uint256 private totalBuys;
    uint256 private totalSells;
    bool private marketSetup;
    bool private internalCall;

    event SharesBought(address indexed account, uint256 amount, uint256 tokenIn);
    event SharesSold(address indexed account, uint256 amount, uint256 tokenOut);
    event SharesRedeemed(address indexed account, uint256 amount, uint256 tokenOut);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyAfterSetup {
        require(marketSetup == true, "Not setup");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyOwnerOrInternal() {
        require(msg.sender == owner || internalCall, "Only owner or self");
        _;
        internalCall = false;
    }

    modifier onlyOracle() {
        require(msg.sender == oracle, "Only oracle");
        _;
    }

    /**
     * @notice Constructor like function for the market maker
     * @param _token ERC-20 token will be used to purchase and redeem rewards for this Market
     */
    function initialize(address _token) public {
        require(owner == address(0) && token == address(0), "Already initialized");
        owner = msg.sender;
        token = _token;
    }

    /**
     * @notice Set up some of the variables for the market maker
     * @param _id The market id should be unique for each master
     * @param _oracle The address for the EOA or contract which will report the result for this Market
     * @param _totalOutcomes The number of different outcomes possible for this market (currently fixed at 2)
     * @param _subsidy How much initial funding is used to seed the market (denominated on initialized token)
     * @param _overround AMM profit margin in bps (Note that 200 represents 2% of the subsidy)
     */
    function setup(uint _id, address _oracle, uint _totalOutcomes, uint _subsidy, uint _overround) public onlyOwner {
        require(!marketSetup, "Already setup");
        require(_overround > 0, "Unsupported overround");

        // Get subsidy tokens
        IERC20(token).safeTransferFrom(msg.sender, address(this), _subsidy);

        // Save basic parameters
        id = _id;
        oracle = _oracle;
        totalOutcomes = _totalOutcomes;

        // Calculate initialization variables
        int128 initialSubsidy = getTokenEth(token, _subsidy);
        int128 n = ABDKMath.fromUInt(_totalOutcomes);
        int128 overround = ABDKMath.divu(_overround, 10000); // Note: if the overround is too low, then exp overflows
        alpha = ABDKMath.div(overround, ABDKMath.mul(n, ABDKMath.ln(n)));
        beta = ABDKMath.mul(ABDKMath.mul(initialSubsidy, n), alpha);
        shares = new int128[](totalOutcomes.add(1));
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            shares[outcome] = initialSubsidy;
            totalShares = ABDKMath.add(totalShares, initialSubsidy);
        }

        // Initialize token leak mitigation of 0.001% (could be a setup parameter in the future)
        // Note: Needed to avoid leaks due to rounding errors on math logarithmic and exponential approximations
        dust = ABDKMath.fromUInt(100000);  // 100k as signed 64.64 bit fixed point

        // Register successful initialization
        marketSetup = true;

        // Optimization: pre calculate current cost to avoid extra calculation on buys and sells
        currentCost = cost();
    }

    /**
     * @notice This function is used to buy outcome tokens.
     * @param _outcome The outcome(s) which a user is buying tokens for (1 for YES, 2 for NO)
     * @param _amount This is the number of outcome tokens purchased (signed 64.64-bit fixed point number)
     * @return _cost The total amount of tokens received from selling the _amount of shares
     */
    function buy(uint256 _outcome, int128 _amount) public onlyAfterSetup returns (uint256 _cost) {
        // Send BUY call to internal function with msg sender
        internalCall = true;
        return _buy(_outcome, _amount, msg.sender);
    }

    /**
     * Internal Buy function used by the public Buy function and PrecogMaster (to facilitate token approvals)
     */
    function _buy(uint256 _outcome, int128 _amount, address _account) public onlyOwnerOrInternal returns (uint256 _cost) {
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");
        require(_amount > 0, "Invalid amount");
        require(block.timestamp >= startTimestamp, "Market not started");
        require(endTimestamp == 0 || block.timestamp <= endTimestamp, "Market already ended");
        require(closeTimestamp == 0, "Market already closed");

        // Add amount of shares to be bought from individual and total shares counters
        shares[_outcome] = ABDKMath.add(shares[_outcome], _amount);
        totalShares = ABDKMath.add(totalShares, _amount);

        // Calculate new BETA parameter (taking into account new total shares and fixed alpha)
        beta = ABDKMath.mul(totalShares, alpha);

        // Calculate new current cost and price to pay. After that, update current cost variable
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        int128 newCost = ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
        int128 deltaCost = ABDKMath.sub(newCost, currentCost);
        currentCost = newCost;

        // Get amount of tokens from sender (as current payment)
        uint tokenCost = getTokenWei(token, deltaCost);
        require(tokenCost > 0, "Invalid cost");
        IERC20(token).safeTransferFrom(msg.sender, address(this), tokenCost);
        uint outcomeShares = getTokenWei(token, _amount);

        // Register BUY in market total and Account details
        totalBuys = totalBuys.add(1);
        accountShares[_account].buys = accountShares[_account].buys.add(1);
        accountShares[_account].deposited = accountShares[_account].deposited.add(tokenCost);
        accountShares[_account].balances[_outcome] = accountShares[_account].balances[_outcome].add(outcomeShares);

        emit SharesBought(_account, outcomeShares, tokenCost);
        return tokenCost;
    }

    /**
     * @notice This function is used to sell outcome tokens already bought.
     * @param _outcome The outcome(s) which a user is buying tokens for (e.g.: 1 for YES, 2 for NO)
     * @param _amount This is the number of outcome tokens purchased (signed 64.64-bit fixed point number)
     * @return _returned The total amount of tokens received from selling the _amount of shares
     */
    function sell(uint256 _outcome, int128 _amount) public onlyAfterSetup returns (uint256 _returned) {
        // Send SELL call to internal function
        internalCall = true;
        return _sell(_outcome, _amount, msg.sender);
    }

    /**
     * Internal Sell function used by the public Sell function and PrecogMaster (to facilitate token approvals)
     */
    function _sell(uint256 _outcome, int128 _amount, address _account) public onlyOwnerOrInternal returns (uint256) {
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");
        require(_amount > 0, "Invalid amount");
        require(block.timestamp >= startTimestamp, "Market not started");
        require(endTimestamp == 0 || block.timestamp <= endTimestamp, "Market already ended");
        require(closeTimestamp == 0, "Market already closed");

        // Remove amount of shares to be sold from individual and total shares counters
        shares[_outcome] = ABDKMath.sub(shares[_outcome], _amount);
        totalShares = ABDKMath.sub(totalShares, _amount);

        // Calculate new BETA parameter (taking into account new total shares and fixed alpha)
        beta = ABDKMath.mul(totalShares, alpha);

        // Calculate new cost and tokens to return. After that, update current cost variable
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        int128 newCost = ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
        int128 deltaCost = ABDKMath.sub(currentCost, newCost);
        int128 deltaDust = ABDKMath.div(deltaCost, dust);  // Token leak mitigation
        currentCost = newCost;

        // Calculate return amount of token to send
        uint tokenReturn = getTokenWei(token, ABDKMath.sub(deltaCost, deltaDust));

        require(tokenReturn > 0, "Invalid return");
        uint outcomeShares = getTokenWei(token, _amount);

        // Check that the received account have the amount of shares to sell
        require(accountShares[_account].balances[_outcome] >= outcomeShares, "Insufficient balance");

        // Register SELL in market total and Account details
        totalSells = totalSells.add(1);
        accountShares[_account].sells = accountShares[_account].sells.add(1);
        accountShares[_account].withdrew = accountShares[_account].withdrew.add(tokenReturn);
        accountShares[_account].balances[_outcome] = accountShares[_account].balances[_outcome].sub(outcomeShares);

        // Transfer collateral tokens to received account
        IERC20(token).safeTransfer(_account, tokenReturn);

        emit SharesSold(_account, outcomeShares, tokenReturn);
        return tokenReturn;
    }

    /**
     * @notice Used to update open market periods
     * @param _startTimestamp Block timestamp when the market start allowing shares trading
     * @param _endTimestamp Block timestamp when the market ends trading and enable report results
     */
    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) public onlyOwner {
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
    }

    /**
     * @notice Used to update oracle address
     * @param _newOracle address of an EOA or contract that can close the market
     */
    function updateOracle(address _newOracle) public onlyOwner {
        require(_newOracle != address(0), "Invalid new oracle");
        oracle = _newOracle;
    }

    /**
     * @notice Used to report results of the market (only for defined oracle)
     * @param _id Unique identifier received on market setup
     * @param _outcome The resulted outcome of the market
     */
    function reportResult(uint256 _id, uint256 _outcome) public onlyOracle {
        // Validate received inputs
        require(_id == id, "Invalid market");
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");

        // Check current state of the market
        require(block.timestamp > endTimestamp, "Market not ended");
        require(closeTimestamp == 0, "Market already closed");

        // Register reported results and register current time
        result = _outcome;
        closeTimestamp = block.timestamp;
    }

    /**
     * @notice Used to redeem token gains (only the market is already closed)
     * @dev The redeem account will be the current msg sender of the tx
     */
    function redeemShares() public onlyAfterSetup returns (uint256 redeemedShares) {
        internalCall = true;
        return _redeem(msg.sender);
    }

    /**
     * @notice Used to redeem token gains in batch (could only be used by the oracle)
     * @dev The list of accounts could be calculated using the "SharesBought" event
     */
    function redeemBatch(address[] memory _accounts) public onlyAfterSetup onlyOracle returns (uint256 redeems) {
        internalCall = true;
        for (uint256 i = 0; i < _accounts.length; i++) {
            try this._redeem(_accounts[i]) {
                redeems.add(1);
            } catch {
                // CASE: `_redeem` call reverted
                // This could be a expected if the account don't have redeemable shares
            }
        }
        return redeems;
    }

    /**
     * Internal Redeem function used by single share redeeming and redeem in batch
     */
    function _redeem(address _account) public onlyOwnerOrInternal returns (uint256 redeemedShares) {
        // Check current state of the market and received account
        require(closeTimestamp > 0, "Market not closed");
        require(accountShares[_account].redeemed == 0, "Shares already redeemed");

        // Get amount of shares to be redeemed for received account
        redeemedShares = accountShares[_account].balances[result];
        require(redeemedShares > 0, "Nothing to redeem");

        // Register amount of shares redeemed and send corresponding collateral tokens (ratio 1:1)
        accountShares[_account].redeemed = redeemedShares;
        IERC20(token).safeTransfer(_account, redeemedShares);

        emit SharesRedeemed(_account, redeemedShares, redeemedShares);
        return redeemedShares;
    }

    /**
     * @notice Used to retrieve any liquidity left on the market (allowed only when market is closed)
     */
    function withdraw(address _token) public onlyAfterSetup onlyOwner {
        require(closeTimestamp > 0, "Market not closed");
        IERC20(_token).safeTransfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }

    /**
     * @notice Admin function to transfer the ownership of this contract
     */
    function transferOwnership(address _newOwner) public virtual onlyOwner {
        require(_newOwner != address(0), "Invalid new owner");
        emit OwnershipTransferred(owner, _newOwner);
        owner = _newOwner;
    }

    /**
     *  @notice Total amount of collateral spent in this Market
     *  For example, if a market was seeded with 2000 PRE and then a further 20 PRE has been spent,
     *  this function will return 2020 PRE.
     */
    function cost() public view onlyAfterSetup returns (int128) {
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        return ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
    }

    /**
     *  This function will tell you the cost (similar to above) after a proposed BUY transaction.
     */
    function costAfterBuy(uint256 _outcome, int128 _amount) internal view returns (int128) {
        int128 newSumTotal;
        int128[] memory newShares = new int128[](shares.length);
        int128 newTotalShares = totalShares;

        // Add new amount of shares to received outcome
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            if (outcome == _outcome) {
                newShares[outcome] = ABDKMath.add(shares[outcome], _amount);
                newTotalShares = ABDKMath.add(newTotalShares, _amount);
            } else {
                newShares[outcome] = shares[outcome];
            }
        }

        // Calculate new cost based on newTotalShares and newQ values
        int128 newBeta = ABDKMath.mul(newTotalShares, alpha);
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            newSumTotal = ABDKMath.add(newSumTotal, ABDKMath.exp(ABDKMath.div(newShares[outcome], newBeta)));
        }
        return ABDKMath.mul(newBeta, ABDKMath.ln(newSumTotal));
    }

    /**
     *  This function will tell you the cost (similar to above) after a proposed SELL transaction.
     */
    function costAfterSell(uint256 _outcome, int128 _amount) internal view returns (int128) {
        int128 newSumTotal;
        int128[] memory newShares = new int128[](shares.length);
        int128 newTotalShares = totalShares;

        // Add new amount of shares to received outcome
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            if (outcome == _outcome) {
                newShares[outcome] = ABDKMath.sub(shares[outcome], _amount);
                newTotalShares = ABDKMath.sub(newTotalShares, _amount);
            } else {
                newShares[outcome] = shares[outcome];
            }
        }

        // Calculate new cost based on newTotalShares and newShares values
        int128 newBeta = ABDKMath.mul(newTotalShares, alpha);
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            newSumTotal = ABDKMath.add(newSumTotal, ABDKMath.exp(ABDKMath.div(newShares[outcome], newBeta)));
        }
        return ABDKMath.mul(newBeta, ABDKMath.ln(newSumTotal));
    }

    /**
     *  This function tells you how much it will cost to make a particular BUY trade.
     *  It does this by calculating the difference between the current cost and the cost after the trade.
     */
    function buyPrice(uint256 _outcome, int128 _amount) public view returns (int128) {
        return ABDKMath.sub(costAfterBuy(_outcome, _amount), currentCost);
    }

    /**
     *  This function tells you how much it will return making a particular SELL trade.
     *  It does this by calculating the difference between the current cost and the cost after the trade.
     */
    function sellPrice(uint256 _outcome, int128 _amount) public view returns (int128) {
        int128 deltaCost = ABDKMath.sub(currentCost, costAfterSell(_outcome, _amount));
        int128 deltaDust = ABDKMath.div(deltaCost, dust);  // token leak mitigation
        return ABDKMath.sub(deltaCost, deltaDust);
    }

    /**
     *  Get current market state information:
     *  total shares (all outcomes), shares balances (by outcome), current liquidity, buys counter, sells counter
     */
    function getMarketInfo() public view returns (int128, int128[] memory, int128, uint256, uint256) {
        int128[] memory sharesBalances = new int128[](totalOutcomes.add(1));

        if (!marketSetup) {
            return (0, sharesBalances, 0, 0, 0);
        }

        // Populate shares balances based on total outcomes configured for this market
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            sharesBalances[outcome] = shares[outcome];
        }

        return (totalShares, sharesBalances, currentCost, totalBuys, totalSells);
    }

    /**
     *  Get amount of shares that an account owns for all outcomes (index 0 should will always be empty)
     */
    function getAccountOutcomeBalances(address _account) public view returns (uint256[] memory) {
        uint256[] memory balances = new uint256[](totalOutcomes.add(1));
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            balances[outcome] = accountShares[_account].balances[outcome];
        }
        return balances;
    }

    /**
     *  Helper function: int128 (signed 64.64 bit fixed point number) [in ether] -> uint256 token amount [in wei]
     */
    function getTokenWei(address _token, int128 _amount) internal view returns (uint256) {
        uint256 d = ERC20(_token).decimals();
        return ABDKMath.mulu(_amount, 10 ** d);
    }

    /**
     *  Helper function: uint256 token amount [in wei] -> int128 (signed 64.64 bit fixed point number) [in ether]
     */
    function getTokenEth(address _token, uint256 _amount) internal view returns (int128) {
        uint256 d = ERC20(_token).decimals();
        return ABDKMath.divu(_amount, 10 ** d);
    }
}

contracts/PrecogMarketV7.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "./ABDKMath64x64.sol";

/**
 * @title PrecogMasterV7: An implementation for liquidity-sensitive LMSR market maker in Solidity
 * @author Marto (https://github.com/0xMarto)
 * @dev Feel free to make any adjustments to the code (DMs are open @0xMarto)
 */
contract PrecogMarketV7 {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct SharesInfo {
        uint256 buys;  // Total amount of Buys
        uint256 sells;  // Total amount of Sells
        uint256 deposited; // Total collateral-in on Buys
        uint256 withdrew; // Total collateral-Out on Sells
        uint256 redeemed; // Total collateral-Out on Redeems
        mapping(uint256 => uint256) balances;  // Shares balances by outcome (has an custom getter)
    }

    // Public variables
    address public owner;  // Should be the PrecogMaster contract
    address public token;  // Collateral to Buy and redeem shares
    uint256 public id;  // Unique Identifier for the market
    uint256 public totalOutcomes; // Amount of outcomes (2 usually mean 1=YES, 2=NO)
    address public oracle;  // EOA or Contract in change to reportResults
    uint256 public startTimestamp;  // Time when Buy/Sell shares are enabled
    uint256 public endTimestamp;  // Time when Buy/Sell shares are disabled
    uint256 public closeTimestamp;  // Time when results were reported
    uint256 public result;  // Final outcome of the market (published by the oracle)
    mapping(address => SharesInfo) public accountShares;  // Account shares balances and info

    // Private variables
    int128[] private shares;  // Amount of shares indexed by outcome (signed 64.64 bit fixed point number)
    int128 private beta;  // LMSR market liquidity variable (signed 64.64 bit fixed point number)
    int128 private alpha;  // Liquidity-Sensitive LMSR market variable (signed 64.64 bit fixed point number)
    int128 private dust;  // Token leak mitigation variable used in share sells (signed 64.64 bit fixed point number)
    int128 private currentCost;  // Current amount of liquidity in the market (signed 64.64 bit fixed point number)
    int128 private totalShares;  // Total amount of shares of all outcomes (signed 64.64 bit fixed point number)
    uint256 private totalBuys;  // Total amount of buys made from all account
    uint256 private totalSells;  // Total amount of sells made from all account
    bool private marketSetup;  // Flag that indicates that a market was configured correctly
    bool private internalCall;  // Flag that indicates that the current call is internal

    // Events emitted
    event SharesBought(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenIn);
    event SharesSold(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenOut);
    event SharesRedeemed(address indexed account, uint256 indexed outcome, uint256 amount, uint256 tokenOut);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Modifiers
    modifier onlyAfterSetup {
        require(marketSetup == true, "Not setup");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyOwnerOrInternal() {
        require(msg.sender == owner || internalCall, "Only owner or self");
        _;
        internalCall = false;
    }

    modifier onlyOracle() {
        require(msg.sender == oracle, "Only oracle");
        _;
    }

    /**
     * @notice Constructor like function for the market maker
     * @param _token ERC-20 token will be used to purchase and redeem rewards for this Market
     */
    function initialize(address _token) public {
        require(owner == address(0) && token == address(0), "Already initialized");
        owner = msg.sender;
        token = _token;
    }

    /**
     * @notice Sets up the market with the specified parameters
     * @param _id The unique identifier for the market
     * @param _oracle The address of the oracle that will report results
     * @param _totalOutcomes The number of possible outcomes for the market
     * @param _subsidy The initial funding used to seed the market (defined on initialized token)
     * @param _overround The AMM profit margin in basis points (bps) [recommended: (100 * _totalOutcomes)]
     */
    function setup(uint _id, address _oracle, uint _totalOutcomes, uint _subsidy, uint _overround) public onlyOwner {
        require(!marketSetup, "Already setup");
        require(_overround > 0, "Unsupported overround");
        require(_totalOutcomes > 0, "Unsupported outcomes");

        // Get initial funding tokens from sender
        IERC20(token).safeTransferFrom(msg.sender, address(this), _subsidy);

        // Save basic parameters
        id = _id;
        oracle = _oracle;
        totalOutcomes = _totalOutcomes;

        // Calculate initialization variables
        int128 initialSubsidy = getTokenEth(token, _subsidy);
        int128 n = ABDKMath.fromUInt(_totalOutcomes);
        int128 overround = ABDKMath.divu(_overround, 10_000); // if the overround is too low the exp function overflows
        alpha = ABDKMath.div(overround, ABDKMath.mul(n, ABDKMath.ln(n)));
        beta = ABDKMath.mul(ABDKMath.mul(initialSubsidy, n), alpha);
        shares = new int128[](totalOutcomes.add(1));
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            shares[outcome] = initialSubsidy;
            totalShares = ABDKMath.add(totalShares, initialSubsidy);
        }

        // Initialize token leak mitigation of 0.001% (could be a setup parameter in the future)
        // Note: Needed to avoid leaks due to rounding errors on math logarithmic and exponential approximations
        dust = ABDKMath.fromUInt(100_000);  // 100k as signed 64.64 bit fixed point

        // Register successful initialization
        marketSetup = true;

        // Optimization: pre calculate current cost to avoid extra calculation on buys and sells
        currentCost = cost();
    }

    /**
     * @notice Buys outcome shares for the specified outcome
     * @param _outcome The outcome of which shares are being bought (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to buy (as a signed 64.64-bit fixed point number)
     * @return tokenCost The total token amount used for buying the specified amount of outcome shares
     */
    function buy(uint256 _outcome, int128 _amount) public onlyAfterSetup returns (uint256 tokenCost) {
        // Send BUY call to internal function with msg sender
        internalCall = true;
        return _buy(_outcome, _amount, msg.sender);
    }

    /**
     * @notice Allows buying market shares for a specified account (limited to only Self or Owner)
     * @param _outcome The outcome of which shares are being bought (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to buy (as a signed 64.64-bit fixed point number)
     * @param _account The address of the buyer
     * @return tokenCost The total token amount used for buying the specified amount of outcome shares
     */
    function _buy(uint256 _outcome, int128 _amount, address _account) public onlyOwnerOrInternal
    returns (uint256 tokenCost) {
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");
        require(_amount > 0, "Invalid amount");
        require(block.timestamp >= startTimestamp, "Market not started");
        require(endTimestamp == 0 || block.timestamp <= endTimestamp, "Market already ended");
        require(closeTimestamp == 0, "Market already closed");

        // Add amount of shares to be bought from individual and total shares counters
        shares[_outcome] = ABDKMath.add(shares[_outcome], _amount);
        totalShares = ABDKMath.add(totalShares, _amount);

        // Calculate new BETA parameter (taking into account new total shares and fixed alpha)
        beta = ABDKMath.mul(totalShares, alpha);

        // Calculate new current cost and price to pay. After that, update current cost variable
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        int128 newCost = ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
        int128 deltaCost = ABDKMath.sub(newCost, currentCost);
        currentCost = newCost;

        // Get amount of tokens from sender (as current payment)
        tokenCost = getTokenWei(token, deltaCost);
        require(tokenCost > 0, "Invalid cost");
        IERC20(token).safeTransferFrom(msg.sender, address(this), tokenCost);
        uint outcomeShares = getTokenWei(token, _amount);

        // Register BUY in market total and Account details
        totalBuys = totalBuys.add(1);
        accountShares[_account].buys = accountShares[_account].buys.add(1);
        accountShares[_account].deposited = accountShares[_account].deposited.add(tokenCost);
        accountShares[_account].balances[_outcome] = accountShares[_account].balances[_outcome].add(outcomeShares);

        emit SharesBought(_account, _outcome, outcomeShares, tokenCost);
        return tokenCost;
    }

    /**
     * @notice Sells outcome shares for the specified outcome
     * @param _outcome The outcome of which shares are being sold (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to sell (as a signed 64.64-bit fixed point number)
     * @return tokenReturn The total amount of tokens received from selling the outcome shares
     */
    function sell(uint256 _outcome, int128 _amount) public onlyAfterSetup returns (uint256 tokenReturn) {
        // Send SELL call to internal function
        internalCall = true;
        return _sell(_outcome, _amount, msg.sender);
    }

    /**
     * @notice Allows selling market shares from a specified account (limited to only Self or Owner)
     * @param _outcome The outcome for which shares are being sold (e.g.: 1 for YES, 2 for NO)
     * @param _amount The number of outcome shares to sell (as a signed 64.64-bit fixed point number)
     * @param _account The address of the seller
     * @return tokenReturn The total amount of tokens received from selling the outcome shares
     */
    function _sell(uint256 _outcome, int128 _amount, address _account) public onlyOwnerOrInternal
    returns (uint256 tokenReturn) {
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");
        require(_amount > 0, "Invalid amount");
        require(block.timestamp >= startTimestamp, "Market not started");
        require(endTimestamp == 0 || block.timestamp <= endTimestamp, "Market already ended");
        require(closeTimestamp == 0, "Market already closed");

        // Remove amount of shares to be sold from individual and total shares counters
        shares[_outcome] = ABDKMath.sub(shares[_outcome], _amount);
        totalShares = ABDKMath.sub(totalShares, _amount);

        // Calculate new BETA parameter (taking into account new total shares and fixed alpha)
        beta = ABDKMath.mul(totalShares, alpha);

        // Calculate new cost and tokens to return. After that, update current cost variable
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        int128 newCost = ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
        int128 deltaCost = ABDKMath.sub(currentCost, newCost);
        int128 deltaDust = ABDKMath.div(deltaCost, dust);  // Token leak mitigation
        currentCost = newCost;

        // Calculate return amount of token to send
        tokenReturn = getTokenWei(token, ABDKMath.sub(deltaCost, deltaDust));

        require(tokenReturn > 0, "Invalid return");
        uint outcomeShares = getTokenWei(token, _amount);

        // Check that the received account have the amount of shares to sell
        require(accountShares[_account].balances[_outcome] >= outcomeShares, "Insufficient balance");

        // Register SELL in market total and Account details
        totalSells = totalSells.add(1);
        accountShares[_account].sells = accountShares[_account].sells.add(1);
        accountShares[_account].withdrew = accountShares[_account].withdrew.add(tokenReturn);
        accountShares[_account].balances[_outcome] = accountShares[_account].balances[_outcome].sub(outcomeShares);

        // Transfer collateral tokens to received account
        IERC20(token).safeTransfer(_account, tokenReturn);

        emit SharesSold(_account, _outcome, outcomeShares, tokenReturn);
        return tokenReturn;
    }

    /**
     * @notice Updates the start and end timestamps for the market (limited to only owner)
     * @param _startTimestamp The timestamp when the market starts allowing trading
     * @param _endTimestamp The timestamp when the market stops allowing trading
     */
    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) public onlyOwner {
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
    }

    /**
     * @notice Update the oracle address of the market (limited to only owner)
     * @param _newOracle The address of the EOA or contract that can close the market
     */
    function updateOracle(address _newOracle) public onlyOwner {
        require(_newOracle != address(0), "Invalid new oracle");
        oracle = _newOracle;
    }

    /**
     * @notice Reports the result of the market (limited to only Oracle)
     * @param _id The unique identifier of the market
     * @param _outcome The outcome that is reported as the result of the market
     */
    function reportResult(uint256 _id, uint256 _outcome) public onlyOracle {
        // Validate received inputs
        require(_id == id, "Invalid market");
        require(_outcome > 0 && _outcome <= totalOutcomes, "Invalid outcome");

        // Check current state of the market
        require(block.timestamp > endTimestamp, "Market not ended");
        require(closeTimestamp == 0, "Market already closed");

        // Register reported results and register current time
        result = _outcome;
        closeTimestamp = block.timestamp;
    }

    /**
     * @notice Redeems the current sender shares for the result of the market
     * @return redeemedShares The number of shares redeemed
     */
    function redeemShares() public onlyAfterSetup returns (uint256 redeemedShares) {
        internalCall = true;
        return _redeem(msg.sender);
    }

    /**
     * @notice Redeems shares in batch for multiple accounts  (limited to only Oracle)
     * @param _accounts The list of accounts to redeem shares for
     * @dev The list of accounts could be calculated using the "SharesBought" event
     * @return redeems The number of successful redeems
     */
    function redeemBatch(address[] memory _accounts) public onlyAfterSetup onlyOracle returns (uint256 redeems) {
        for (uint256 i = 0; i < _accounts.length; i++) {
            internalCall = true;
            try this._redeem(_accounts[i]) {
                redeems.add(1);
            } catch {
                // CASE: `_redeem` call reverted (expected if the account don't have redeemable shares)
                internalCall = false; // Disable internal just incase of revert
            }
        }
        return redeems;
    }

    /**
     * @notice Redeems the received account shares for the result of the market (limited to only Self or Owner)
     * @param _account The address of the account with shares of the market
     * @return redeemedShares The number of shares redeemed
     */
    function _redeem(address _account) public onlyOwnerOrInternal returns (uint256 redeemedShares) {
        // Check current state of the market and received account
        require(closeTimestamp > 0, "Market not closed");
        require(accountShares[_account].redeemed == 0, "Shares already redeemed");

        // Get amount of shares to be redeemed for received account
        redeemedShares = accountShares[_account].balances[result];
        require(redeemedShares > 0, "Nothing to redeem");

        // Register amount of shares redeemed and send corresponding collateral tokens (ratio 1:1)
        accountShares[_account].redeemed = redeemedShares;
        IERC20(token).safeTransfer(_account, redeemedShares);

        emit SharesRedeemed(_account, result, redeemedShares, redeemedShares);
        return redeemedShares;
    }

    /**
     * @notice Withdraws any remaining liquidity from the market (limited to only owner)
     * @param _token The address of the ERC-20 token to withdraw
     */
    function withdraw(address _token) public onlyAfterSetup onlyOwner {
        require(closeTimestamp > 0, "Market not closed");
        IERC20(_token).safeTransfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }

    /**
     * @notice Transfers the ownership of the contract to a new owner (limited to only current owner)
     * @param _newOwner The address of the new owner
     */
    function transferOwnership(address _newOwner) public virtual onlyOwner {
        require(_newOwner != address(0), "Invalid new owner");
        emit OwnershipTransferred(owner, _newOwner);
        owner = _newOwner;
    }

    /**
     * @notice Gets the total collateral spent in the market
     * @return totalCost The total cost in the form of a signed 64.64-bit fixed point number
     */
    function cost() public view onlyAfterSetup returns (int128 totalCost) {
        int128 sumTotal;
        for (uint outcome = 1; outcome <= totalOutcomes; outcome++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(shares[outcome], beta)));
        }
        return ABDKMath.mul(beta, ABDKMath.ln(sumTotal));
    }

    /**
     * @notice Gets the cost of buying the specified amount of outcome shares
     * @param _outcome The outcome for which shares are being bought
     * @param _amount The number of outcome shares to buy (as signed 64.64-bit fixed point number)
     * @return tokenCost The token cost amount (as a signed 64.64-bit fixed point number)
     */
    function buyPrice(uint256 _outcome, int128 _amount) public view returns (int128 tokenCost) {
        return ABDKMath.sub(costAfterBuy(_outcome, _amount), currentCost);
    }

    /**
     * @notice Gets the return from selling the specified amount of outcome shares
     * @param _outcome The outcome for which shares are being sold
     * @param _amount The number of outcome shares to sell (as signed 64.64-bit fixed point number)
     * @return tokenReturn The token return amount (as a signed 64.64-bit fixed point number)
     */
    function sellPrice(uint256 _outcome, int128 _amount) public view returns (int128 tokenReturn) {
        int128 deltaCost = ABDKMath.sub(currentCost, costAfterSell(_outcome, _amount));
        int128 deltaDust = ABDKMath.div(deltaCost, dust);  // token leak mitigation
        return ABDKMath.sub(deltaCost, deltaDust);
    }

    /**
     * @notice Gets the current market state information
     * @return totalShares The current total shares minted for all outcomes of the market
     * @return sharesBalances All shares balances (indexed by outcome)
     * @return currentCost The current liquidity of the market
     * @return totalBuys Buys counter of the market
     * @return totalSells Sells counter of the market
     */
    function getMarketInfo() public view returns (int128, int128[] memory, int128, uint256, uint256) {
        int128[] memory sharesBalances = new int128[](totalOutcomes.add(1));

        if (!marketSetup) {
            return (0, sharesBalances, 0, 0, 0);
        }

        // Populate shares balances based on total outcomes configured for this market
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            sharesBalances[outcome] = shares[outcome];
        }

        return (totalShares, sharesBalances, currentCost, totalBuys, totalSells);
    }

    /**
     * @notice Gets current market buy and sell prices for all outcomes
     * @dev Helper function to fast calculate market prediction and spreads
     * @return buyPrices buy price of 1 share for all outcomes (indexed by outcome)
     * @return sellPrices sell price of 1 share for all outcomes (indexed by outcome)
     */
    function getPrices() public view returns (uint256[] memory buyPrices, uint256[] memory sellPrices) {
        buyPrices = new uint256[](totalOutcomes.add(1));
        sellPrices = new uint256[](totalOutcomes.add(1));
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            int128 oneShare = 18446744073709551616;  // 1 share as int128 (signed 64.64 bit fixed point number)
            buyPrices[outcome] = getTokenWei(token, buyPrice(outcome, oneShare));
            sellPrices[outcome] = getTokenWei(token, sellPrice(outcome, oneShare));
        }
        return (buyPrices, sellPrices);
    }

    /**
     * @notice Gets the amount of shares that an account owns for all outcomes
     * @param _account The address of the account with shares of the market
     * @return balances The balances of shares for all outcomes (indexed by outcome)
     */
    function getAccountOutcomeBalances(address _account) public view returns (uint256[] memory balances) {
        balances = new uint256[](totalOutcomes.add(1));
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            balances[outcome] = accountShares[_account].balances[outcome];
        }
        return balances;
    }

    /**
     *  @notice Gets the total collateral spent in the market after a received BUY trade
     *  @dev Internal function used to calculate buy price
     */
    function costAfterBuy(uint256 _outcome, int128 _amount) internal view returns (int128) {
        int128 newSumTotal;
        int128[] memory newShares = new int128[](shares.length);
        int128 newTotalShares = totalShares;

        // Add new amount of shares to received outcome
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            if (outcome == _outcome) {
                newShares[outcome] = ABDKMath.add(shares[outcome], _amount);
                newTotalShares = ABDKMath.add(newTotalShares, _amount);
            } else {
                newShares[outcome] = shares[outcome];
            }
        }

        // Calculate new cost based on newTotalShares and newQ values
        int128 newBeta = ABDKMath.mul(newTotalShares, alpha);
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            newSumTotal = ABDKMath.add(newSumTotal, ABDKMath.exp(ABDKMath.div(newShares[outcome], newBeta)));
        }
        return ABDKMath.mul(newBeta, ABDKMath.ln(newSumTotal));
    }

    /**
     *  @notice Gets the total collateral spent in the market after a received SELL trade
     *  @dev Internal function used to calculate sell price
     */
    function costAfterSell(uint256 _outcome, int128 _amount) internal view returns (int128) {
        int128 newSumTotal;
        int128[] memory newShares = new int128[](shares.length);
        int128 newTotalShares = totalShares;

        // Add new amount of shares to received outcome
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            if (outcome == _outcome) {
                newShares[outcome] = ABDKMath.sub(shares[outcome], _amount);
                newTotalShares = ABDKMath.sub(newTotalShares, _amount);
            } else {
                newShares[outcome] = shares[outcome];
            }
        }

        // Calculate new cost based on newTotalShares and newShares values
        int128 newBeta = ABDKMath.mul(newTotalShares, alpha);
        for (uint256 outcome = 1; outcome <= totalOutcomes; outcome++) {
            newSumTotal = ABDKMath.add(newSumTotal, ABDKMath.exp(ABDKMath.div(newShares[outcome], newBeta)));
        }
        return ABDKMath.mul(newBeta, ABDKMath.ln(newSumTotal));
    }

    /**
     *  @notice Translate int128 (signed 64.64 bit fixed point number) [in ether] to uint256 token amount [in wei]
     *  @dev Internal function used to transform encoded number values
     */
    function getTokenWei(address _token, int128 _amount) internal view returns (uint256) {
        uint256 decimals = ERC20(_token).decimals();
        return ABDKMath.mulu(_amount, 10 ** decimals);
    }

    /**
     *  @notice Translate uint256 token amount [in wei] to int128 (signed 64.64 bit fixed point number) [in ether]
     *  @dev Internal function used to transform encoded number values
     */
    function getTokenEth(address _token, uint256 _amount) internal view returns (int128) {
        uint256 decimals = ERC20(_token).decimals();
        return ABDKMath.divu(_amount, 10 ** decimals);
    }
}

contracts/PrecogMasterV6.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;
//import "hardhat/console.sol";

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./IPrecogToken.sol";
import "./IPrecogMarket.sol";

/**
 * @title PrecogMasterV6: Contract to manage PrecogMarkets interactions and PrecogToken claims
 * @author Marto
 * @dev Feel free to make any adjustments to the code
 */
contract PrecogMasterV6 is AccessControl {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CALLER_ROLE = keccak256("CALLER_ROLE");
    bytes32 public constant MARKET_CREATOR_ROLE = keccak256("MARKET_CREATOR_ROLE");

    struct TokenClaim {
        address account;
        uint256 user;
        uint256 claimedAmount;
        uint256 claimedTimestamp;
    }

    struct MiningSeason {
        uint256 startTimestamp;
        uint256 endTimestamp;
        uint256 maxUserClaim;
        uint256 maxTotalClaim;
        uint256 maxTotalMint;
        uint256 claimedAmount;
        uint256 mintedAmount;
    }

    struct MarketInfo {
        string name;
        string description;
        string category;
        uint256 startTimestamp;
        uint256 endTimestamp;
        address creator;
        address market;
    }

    struct MarketConfig {
        uint256 totalOutcomes;
        uint256 funding;
        uint256 overround;
        address collateralToken;
        address collateralFunder;
        address marketOracle;
    }

    address public token;
    uint256 public createdMarkets;
    uint256 public currentSeason;
    address private market; // Used as recipe for all created markets
    address private oracle; // Used as default for all non custom markets

    mapping(address => TokenClaim) public accountTokenClaims;
    mapping(uint256 => TokenClaim) public userTokenClaims;
    mapping(uint256 => MiningSeason) public miningSeasons;
    mapping(uint256 => MarketInfo) public markets;

    event TokensClaimed(address indexed account, uint256 indexed user, uint256 amount, uint256 timestamp);
    event TokensMinted(address indexed account, uint256 amount, uint256 timestamp);
    event MarketCreated(address indexed creator, uint256 id, address market);

    constructor(address precogToken, address initialAdmin) {
        // Grant DEFAULT_ADMIN_ROLE to the initial admin (this is the admin to the ADMIN_ROLE list)
        _setupRole(DEFAULT_ADMIN_ROLE, initialAdmin);
        // Set ADMIN_ROLE as admin of CALLER_ROLE list
        _setRoleAdmin(CALLER_ROLE, ADMIN_ROLE);
        // Set already deployed precogToken as claimable token and collateral token for all Markets
        token = precogToken;
        // Grant ADMIN_ROLE to initial Admin (this enables to call "addAdmin" helper function)
        _setupRole(ADMIN_ROLE, initialAdmin);
    }

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "Only Admin");
        _;
    }

    modifier onlyCaller() {
        require(hasRole(CALLER_ROLE, msg.sender), "Only Caller");
        _;
    }

    modifier onlyMarketCreator() {
        require(hasRole(MARKET_CREATOR_ROLE, msg.sender), "Only Market Creator");
        _;
    }

    // Public functions to interact with created markets
    function marketBuy(
        uint256 marketId,
        uint256 outcome,
        int128 sharesAmount,
        uint256 maxAmountIn
    ) external returns (uint256 amountIn) {
        require(block.timestamp >= markets[marketId].startTimestamp, 'Market not started');
        require(block.timestamp <= markets[marketId].endTimestamp, 'Market already ended');

        // Get maxAmountIn of tokens from the buyer to Master (this reverts if there is no balance on buyer)
        address marketCollateral = IPrecogMarket(markets[marketId].market).token();
        if (marketCollateral == token) {
            // Case PrecogToken market: just move the max amount to this contract (no approve needed)
            IPrecogToken(token).move(msg.sender, address(this), maxAmountIn);
        } else {
            // Case custom token market: try to transfer from sender (this reverts is there is no allowance)
            IERC20(marketCollateral).safeTransferFrom(msg.sender, address(this), maxAmountIn);
        }

        // Send remote BUY to market contract (to be assigned to the sender)
        amountIn = IPrecogMarket(markets[marketId].market)._buy(outcome, sharesAmount, msg.sender);
        require(amountIn <= maxAmountIn, "Max amount reach!");  // Just in case there is some balance on this contract

        // If there is any leftover, return tokens to sender
        if (amountIn < maxAmountIn) {
            IERC20(marketCollateral).safeTransfer(msg.sender, maxAmountIn.sub(amountIn));
        }
        return amountIn;
    }

    function marketSell(
        uint256 marketId,
        uint256 outcome,
        int128 sharesAmount,
        uint256 minAmountOut
    ) external returns (uint256 amountOut) {
        require(block.timestamp >= markets[marketId].startTimestamp, "Market not started");
        require(block.timestamp <= markets[marketId].endTimestamp, "Market already ended");

        // Send remote SELL to market contract (to be assigned to the sender)
        amountOut = IPrecogMarket(markets[marketId].market)._sell(outcome, sharesAmount, msg.sender);

        // Check that the sell price was equal or higher seller expected
        require(amountOut >= minAmountOut, "Min amount reach!");

        return amountOut;
    }

    function marketRedeemShares(uint256 marketId) external returns (uint256 shares) {
        return IPrecogMarket(markets[marketId].market)._redeem(msg.sender);
    }

    function marketBuyPrice(uint256 marketId, uint256 outcome, int128 sharesAmount) external view returns (int128) {
        return IPrecogMarket(markets[marketId].market).buyPrice(outcome, sharesAmount);
    }

    function marketSellPrice(uint256 marketId, uint256 outcome, int128 sharesAmount) external view returns (int128) {
        return IPrecogMarket(markets[marketId].market).sellPrice(outcome, sharesAmount);
    }

    function marketAccountShares(uint256 marketId, address account) external view
    returns (uint256 buys, uint256 sells, uint256 deposited, uint256 withdrew, uint256 redeemed,
        uint256[] memory balances) {
        // Get market based on received id (should be valid id)
        IPrecogMarket createdMarket = IPrecogMarket(markets[marketId].market);
        // Get sharesInfo on received account for selected market
        (buys, sells, deposited, withdrew, redeemed) = createdMarket.accountShares(account);
        // Get outcome balances on received account for selected market
        balances = createdMarket.getAccountOutcomeBalances(account);
    }

    function marketSharesInfo(uint256 marketId) external view
    returns (int128 totalShares, int128[] memory sharesBalances, int128 cost, uint256 totalBuys, uint256 totalSells) {
        // Get market based on received id (should be valid id)
        IPrecogMarket createdMarket = IPrecogMarket(markets[marketId].market);
        (totalShares, sharesBalances, cost, totalBuys, totalSells) = createdMarket.getMarketInfo();
    }

    // Public helper view function
    function hasRedeemedShares(uint256 marketId, address account) external view returns (bool) {
        (,,,,uint256 redeemed) = IPrecogMarket(markets[marketId].market).accountShares(account);
        return redeemed > 0;
    }

    function hadClaimed(address account, uint256 user) external view returns (bool) {
        return accountTokenClaims[account].claimedTimestamp != 0 || userTokenClaims[user].claimedTimestamp != 0;
    }

    // Only caller functions
    function claimToken(address account, uint256 user, uint256 amount) external onlyCaller returns (bool) {
        // Validate token claim
        require(accountTokenClaims[account].claimedTimestamp == 0, "Account already claimed");
        require(userTokenClaims[user].claimedTimestamp == 0, "User already claimed");
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(block.timestamp >= season.startTimestamp, "Season not started");
            require(block.timestamp < season.endTimestamp, "Season already ended");
            require(amount <= season.maxUserClaim, "Invalid user claim amount");
            require(amount.add(season.claimedAmount) <= season.maxTotalClaim, "Season max token claims");
            require(amount.add(season.mintedAmount) <= season.maxTotalMint, "Season max token mints");
        }

        // Register current token claim
        TokenClaim memory claim = TokenClaim({
            account: account,
            user: user,
            claimedAmount: amount,
            claimedTimestamp: block.timestamp
        });
        accountTokenClaims[account] = claim;
        userTokenClaims[user] = claim;
        season.claimedAmount = season.claimedAmount.add(amount);
        season.mintedAmount = season.mintedAmount.add(amount);

        // Mint tokens to received account
        IPrecogToken(token).mint(account, amount);

        emit TokensClaimed(account, user, amount, block.timestamp);
        return true;
    }

    function createMarket(
        string calldata name,
        string calldata description,
        string calldata category,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address creator,
        uint256 totalOutcomes,
        uint256 funding,
        uint256 overround
    ) external onlyCaller returns (uint256 newMarketId) {
        // Mint tokens to seed the new market
        IPrecogToken(token).mint(address(this), funding);

        // Pack all received information (with defaults), create new market and return new market id
        MarketInfo memory marketInfo = MarketInfo(
            name, description, category, startTimestamp, endTimestamp, creator, address(0)
        );
        MarketConfig memory marketConfig = MarketConfig(
            totalOutcomes,
            funding,
            overround,
            token,  // collateralToken: Precog Token
            address(this),  // collateralFunder: This contract
            oracle  // marketOracle: Preset oracle
        );
        return _createMarket(marketInfo, marketConfig);
    }

    function createCustomMarket(
        string memory name,
        string memory description,
        string memory category,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address creator,
        uint256 totalOutcomes,
        uint256 funding,
        uint256 overround,
        address collateralToken,
        address collateralFunder,
        address marketOracle
    ) public onlyMarketCreator returns (uint256 newMarketId) {
        // Pack all received information, create new market and return new market id
        MarketInfo memory marketInfo = MarketInfo(
            name, description, category, startTimestamp, endTimestamp, creator, address(0)
        );
        MarketConfig memory marketConfig = MarketConfig(
            totalOutcomes, funding, overround, collateralToken, collateralFunder, marketOracle
        );
        return _createMarket(marketInfo, marketConfig);
    }

    function _createMarket(MarketInfo memory info, MarketConfig memory config) internal returns (uint256 newMarketId) {
        // Deploy a new market contract and initialize it with the collateral token
        address newMarketAddress = Clones.clone(market);
        IPrecogMarket newMarket = IPrecogMarket(newMarketAddress);
        newMarket.initialize(config.collateralToken);

        // Get funding amount to seed the new market (optimization: except in a self-funder usecase)
        if (config.collateralFunder != address(this)) {
            IERC20(config.collateralToken).safeTransferFrom(config.collateralFunder, address(this), config.funding);
        }
        // Pre approve all trades with
        IERC20(config.collateralToken).approve(newMarketAddress, type(uint256).max);

        // Get new Id and Setup deployed new market
        newMarketId = createdMarkets;
        newMarket.setup(newMarketId, config.marketOracle, config.totalOutcomes, config.funding, config.overround);
        newMarket.updateDates(info.startTimestamp, info.endTimestamp);

        // Save created market address in received info and save it in local storage (indexed by id)
        info.market = newMarketAddress;
        markets[newMarketId] = info;

        // Increase created markets counter and return new market id
        createdMarkets = createdMarkets.add(1);

        emit MarketCreated(info.creator, newMarketId, newMarketAddress);
        return newMarketId;
    }

    // Only admin functions
    function addMarketCreator(address account) external onlyAdmin {
        grantRole(MARKET_CREATOR_ROLE, account);
    }

    function removeMarketCreator(address account) external onlyAdmin {
        revokeRole(MARKET_CREATOR_ROLE, account);
    }

    function addCaller(address account) external onlyAdmin {
        grantRole(CALLER_ROLE, account);
    }

    function removeCaller(address account) external onlyAdmin {
        revokeRole(CALLER_ROLE, account);
    }

    function addAdmin(address account) external onlyAdmin {
        grantRole(ADMIN_ROLE, account);
    }

    function removeAdmin(address account) external onlyAdmin {
        revokeRole(ADMIN_ROLE, account);
    }

    function updateCurrentSeason(uint256 seasonIndex) external onlyAdmin {
        currentSeason = seasonIndex;
    }

    function setMiningSeason(
        uint256 seasonIndex,
        uint256 startTimestamp,
        uint256 endTimestamp,
        uint256 maxUserClaim,
        uint256 maxTotalClaim,
        uint256 maxTotalMint,
        uint256 claimedAmount,
        uint256 mintedAmount
    ) external onlyAdmin {
        miningSeasons[seasonIndex] = MiningSeason({
            startTimestamp: startTimestamp,
            endTimestamp: endTimestamp,
            maxUserClaim: maxUserClaim,
            maxTotalClaim: maxTotalClaim,
            maxTotalMint: maxTotalMint,
            claimedAmount: claimedAmount,
            mintedAmount: mintedAmount
        });
    }

    function setBaseOracle(address _oracle) external onlyAdmin {
        oracle = _oracle;
    }

    function setBaseMarket(address _market) external onlyAdmin {
        market = _market;
    }

    function updateMarket(
        uint256 id,
        string memory name,
        string memory description,
        string memory category,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address marketCreator,
        address marketOracle
    ) external onlyAdmin {
        if (bytes(name).length > 0) {
            markets[id].name = name;
        }
        if (bytes(description).length > 0) {
            markets[id].description = description;
        }
        if (bytes(category).length > 0) {
            markets[id].category = category;
        }
        if (marketCreator != address(0)) {
            markets[id].creator = marketCreator;
        }
        if (marketOracle != address(0)) {
            IPrecogMarket(markets[id].market).updateOracle(marketOracle);
        }
        bool updateDates = false;
        if (startTimestamp > 0) {
            markets[id].startTimestamp = startTimestamp;
            updateDates = true;
        }
        if (endTimestamp > 0) {
            markets[id].endTimestamp = endTimestamp;
            updateDates = true;
        }
        if (updateDates) {
            IPrecogMarket(markets[id].market).updateDates(markets[id].startTimestamp, markets[id].endTimestamp);
        }
    }

    function marketWithdraw(uint256 marketId, address marketToken) external onlyAdmin {
        IPrecogMarket(markets[marketId].market).withdraw(marketToken);
    }

    function marketTransferOwnership(uint256 marketId, address newOwner) external onlyAdmin {
        IPrecogMarket(markets[marketId].market).transferOwnership(newOwner);
    }

    function updateTokenClaim(address account, uint256 amount, uint256 timestamp) external onlyAdmin {
        // Used to reset token claims
        accountTokenClaims[account].claimedAmount = amount;
        accountTokenClaims[account].claimedTimestamp = timestamp;
        uint256 user = accountTokenClaims[account].user;
        userTokenClaims[user].claimedAmount = amount;
        userTokenClaims[user].claimedTimestamp = timestamp;
    }

    function precogMint(address to, uint256 amount) external onlyAdmin {
        // Validate token mint
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(amount.add(season.mintedAmount) <= season.maxTotalMint, "Season max token mints");
        }

        // Register token mint
        season.mintedAmount = season.mintedAmount.add(amount);

        // Mint tokens to received account
        IPrecogToken(token).mint(to, amount);

        emit TokensMinted(to, amount, block.timestamp);
    }

    function precogBurn(address from, uint256 amount) external onlyAdmin {
        // Here we should decrease `season.mintedAmount` for the current season
        IPrecogToken(token).burn(from, amount);
    }

    function precogTransferOwnership(address newPrecogMaster) external onlyAdmin {
        IPrecogToken(token).transferOwnership(newPrecogMaster);
    }

    function withdraw(address _token) public onlyAdmin {
        IERC20(_token).safeTransfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }
}

contracts/PrecogMasterV7.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./IPrecogToken.sol";
import "./IPrecogMarket.sol";

/**
 * @title PrecogMaster: Manager of prediction markets and Precog token claims
 * @author Marto (https://github.com/0xMarto)
 * @dev Feel free to make any adjustments to the code (DMs are open @0xMarto)
 */
contract PrecogMasterV7 is AccessControl {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CALLER_ROLE = keccak256("CALLER_ROLE");
    bytes32 public constant MARKET_CREATOR_ROLE = keccak256("MARKET_CREATOR_ROLE");

    // State objects
    struct TokenClaim {
        address account;
        uint256 user;
        uint256 claimedAmount;
        uint256 claimedTimestamp;
    }

    struct MiningSeason {
        uint256 startTimestamp;
        uint256 endTimestamp;
        uint256 maxUserClaim;
        uint256 maxTotalClaim;
        uint256 maxTotalMint;
        uint256 claimedAmount;
        uint256 mintedAmount;
    }

    struct MarketInfo {
        string name;
        string description;
        string category;
        string outcomes;
        uint256 startTimestamp;
        uint256 endTimestamp;
        address creator;
        address market;
    }

    struct MarketConfig {
        uint256 totalOutcomes;
        uint256 funding;
        uint256 overround;
        address collateralToken;
        address collateralFunder;
        address marketOracle;
    }

    // Public variables
    address public token;  // Claim token and default collateral for non custom markets
    address private oracle; // default oracle for all non custom markets
    address private market; // base market recipe for all created markets
    uint256 public createdMarkets;  // Total markets created
    uint256 public currentSeason;  // Current claiming season number
    mapping(address => TokenClaim) public accountTokenClaims;
    mapping(uint256 => TokenClaim) public userTokenClaims;
    mapping(uint256 => MiningSeason) public miningSeasons;
    mapping(uint256 => MarketInfo) public markets;

    // Events emitted
    event TokensClaimed(address indexed account, uint256 indexed user, uint256 amount, uint256 timestamp);
    event TokensMinted(address indexed account, uint256 amount, uint256 timestamp);
    event MarketCreated(address indexed creator, uint256 id, address market);

    // Modifiers
    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "Only Admin");
        _;
    }

    modifier onlyCaller() {
        require(hasRole(CALLER_ROLE, msg.sender), "Only Caller");
        _;
    }

    modifier onlyMarketCreator() {
        require(hasRole(MARKET_CREATOR_ROLE, msg.sender), "Only Market Creator");
        _;
    }

    // Functions
    constructor(address precogToken, address initialAdmin) {
        // Grant DEFAULT_ADMIN_ROLE to the initial admin (this is the admin to the ADMIN_ROLE list)
        _setupRole(DEFAULT_ADMIN_ROLE, initialAdmin);
        // Set ADMIN_ROLE as admin of CALLER_ROLE list
        _setRoleAdmin(CALLER_ROLE, ADMIN_ROLE);
        // Set already deployed precogToken as claimable token and collateral token for all Markets
        token = precogToken;
        // Grant ADMIN_ROLE to initial Admin (this enables to call "addAdmin" helper function)
        _setupRole(ADMIN_ROLE, initialAdmin);
    }

    /**
     * @notice Buys shares for the specified outcome in the desired market
     * @param marketId unique market identifier to trade
     * @param outcome The outcome of which shares are being bought (e.g.: 1 for YES, 2 for NO)
     * @param sharesAmount Number of outcome shares to buy (as a signed 64.64-bit fixed point number)
     * @param maxAmountIn Max amount of tokens able to spend in this trade (front-run mitigation)
     * @return amountIn Token amount used for buying the specified amount of shares
     */
    function marketBuy(
        uint256 marketId,
        uint256 outcome,
        int128 sharesAmount,
        uint256 maxAmountIn
    ) external returns (uint256 amountIn) {
        require(block.timestamp >= markets[marketId].startTimestamp, 'Market not started');
        require(block.timestamp <= markets[marketId].endTimestamp, 'Market already ended');

        // Get maxAmountIn of tokens from the buyer to Master (this reverts if there is no balance on buyer)
        address marketCollateral = IPrecogMarket(markets[marketId].market).token();
        if (marketCollateral == token) {
            // Case PrecogToken market: just move the max amount to this contract (no approve needed)
            IPrecogToken(token).move(msg.sender, address(this), maxAmountIn);
        } else {
            // Case custom token market: try to transfer from sender (this reverts is there is no allowance)
            IERC20(marketCollateral).safeTransferFrom(msg.sender, address(this), maxAmountIn);
        }

        // Send remote BUY to market contract (to be assigned to the sender)
        amountIn = IPrecogMarket(markets[marketId].market)._buy(outcome, sharesAmount, msg.sender);
        require(amountIn <= maxAmountIn, "Max amount reach!");  // Just in case there is some balance on this contract

        // If there is any leftover, return tokens to sender
        if (amountIn < maxAmountIn) {
            IERC20(marketCollateral).safeTransfer(msg.sender, maxAmountIn.sub(amountIn));
        }
        return amountIn;
    }

    /**
     * @notice Sells shares for the specified outcome in the desired market
     * @param marketId unique market identifier to trade
     * @param outcome The outcome of which shares are being sold (e.g.: 1 for YES, 2 for NO)
     * @param sharesAmount Number of outcome shares to sell (as a signed 64.64-bit fixed point number)
     * @param minAmountOut Min amount of tokens to obtain in this trade (front-run mitigation)
     * @return amountOut Token amount obtain from selling the specified amount of shares
     */
    function marketSell(
        uint256 marketId,
        uint256 outcome,
        int128 sharesAmount,
        uint256 minAmountOut
    ) external returns (uint256 amountOut) {
        require(block.timestamp >= markets[marketId].startTimestamp, "Market not started");
        require(block.timestamp <= markets[marketId].endTimestamp, "Market already ended");

        // Send remote SELL to market contract (to be assigned to the sender)
        amountOut = IPrecogMarket(markets[marketId].market)._sell(outcome, sharesAmount, msg.sender);

        // Check that the sell price was equal or higher seller expected
        require(amountOut >= minAmountOut, "Min amount reach!");

        return amountOut;
    }

    /**
     * @notice Redeems the total sender shares in the desired market
     * @param marketId unique market identifier to trade
     * @return shares Number of shares redeemed
     */
    function marketRedeemShares(uint256 marketId) external returns (uint256 shares) {
        return IPrecogMarket(markets[marketId].market)._redeem(msg.sender);
    }

    /**
     * @notice Gets the cost of buying the specified amount of outcome shares in the desired market
     * @param marketId unique market identifier to trade
     * @param outcome The outcome for which tokens are being bought
     * @param sharesAmount Number of outcome shares to buy (as signed 64.64-bit fixed point number)
     * @return tokenCost The token cost amount (as a signed 64.64-bit fixed point number)
     */
    function marketBuyPrice(uint256 marketId, uint256 outcome, int128 sharesAmount) external view
    returns (int128 tokenCost) {
        return IPrecogMarket(markets[marketId].market).buyPrice(outcome, sharesAmount);
    }

    /**
     * @notice Gets the return from selling the specified amount of outcome shares in the desired market
     * @param marketId unique market identifier to trade
     * @param outcome The outcome for which shares are being sold
     * @param sharesAmount The number of outcome shares to sell (as signed 64.64-bit fixed point number)
     * @return tokenReturn The token return amount (as a signed 64.64-bit fixed point number)
     */
    function marketSellPrice(uint256 marketId, uint256 outcome, int128 sharesAmount) external view
    returns (int128 tokenReturn) {
        return IPrecogMarket(markets[marketId].market).sellPrice(outcome, sharesAmount);
    }

    /**
     * @notice Gets market buy and sell prices for all outcomes in the desired market
     * @dev Helper function to fast calculate market prediction and spreads
     * @param marketId unique market identifier to trade
     * @return buyPrices buy price of 1 share for all outcomes (indexed by outcome)
     * @return sellPrices sell price of 1 share for all outcomes (indexed by outcome)
     */
    function marketPrices(uint256 marketId) external view
    returns (uint256[] memory buyPrices, uint256[] memory sellPrices) {
        return IPrecogMarket(markets[marketId].market).getPrices();
    }

    /**
     * @notice Gets market result summary of the desired market
     * @dev Helper function to show closed market info
     * @param marketId unique market identifier to trade
     * @return result Reported market result outcome
     * @return closed Timestamp when the market result was reported
     * @return reporter Address of the market result reporter (market oracle)
     */
    function marketResultInfo(uint256 marketId) external view
    returns (uint256 result, uint256 closed, address reporter) {
        IPrecogMarket createdMarket = IPrecogMarket(markets[marketId].market);

        // Get market result info
        result = createdMarket.result();
        closed = createdMarket.closeTimestamp();
        reporter = createdMarket.oracle();
        return (result, closed, reporter);
    }

    /**
     * @notice Gets market account shares summary of the desired market
     * @dev Helper function to show market info about an specific account
     * @param marketId unique market identifier to trade
     * @param account The address of the account with shares of the market
     * @return buys Total amount of Buys in the market
     * @return sells Total amount of Sells in the market
     * @return deposited Total amount of collateral deposited in the market
     * @return withdrew Total amount of collateral withdrew from the market
     * @return redeemed Total amount of collateral redeemed from the market
     * @return balances Account shares balances by outcome
     */
    function marketAccountShares(uint256 marketId, address account) external view
    returns (uint256 buys, uint256 sells, uint256 deposited, uint256 withdrew, uint256 redeemed,
        uint256[] memory balances) {
        IPrecogMarket createdMarket = IPrecogMarket(markets[marketId].market);
        // Get sharesInfo on received account for selected market
        (buys, sells, deposited, withdrew, redeemed) = createdMarket.accountShares(account);
        // Get outcome balances on received account for selected market
        balances = createdMarket.getAccountOutcomeBalances(account);
    }

    /**
     * @notice Gets the current market state info
     * @dev Helper function to show general market shares info
     * @param marketId unique market identifier to trade
     * @return totalShares Current total shares minted for all outcomes of the market
     * @return sharesBalances All shares balances (indexed by outcome)
     * @return cost Current liquidity of the market
     * @return totalBuys Buys counter of the market
     * @return totalSells Sells counter of the market
     */
    function marketSharesInfo(uint256 marketId) external view
    returns (int128 totalShares, int128[] memory sharesBalances, int128 cost, uint256 totalBuys, uint256 totalSells) {
        IPrecogMarket createdMarket = IPrecogMarket(markets[marketId].market);
        (totalShares, sharesBalances, cost, totalBuys, totalSells) = createdMarket.getMarketInfo();
    }

    /**
     * @notice Helper function to check market close state
     */
    function isClosedMarket(uint256 marketId) external view returns (bool) {
        return IPrecogMarket(markets[marketId].market).closeTimestamp() > block.timestamp;
    }

    /**
     * @notice Helper function to check if some account has already redeemed market shares
     */
    function hasRedeemedShares(uint256 marketId, address account) external view returns (bool) {
        (,,,,uint256 redeemed) = IPrecogMarket(markets[marketId].market).accountShares(account);
        return redeemed > 0;
    }

    /**
     * @notice Helper function to check if some account has already claim current season tokens
     */
    function hasClaimed(address account, uint256 user) external view returns (bool) {
        return accountTokenClaims[account].claimedTimestamp != 0 || userTokenClaims[user].claimedTimestamp != 0;
    }

    // Whitelisted functions: Only caller & Only market creator
    function claimToken(address account, uint256 user, uint256 amount) external onlyCaller returns (bool) {
        // Validate token claim
        require(accountTokenClaims[account].claimedTimestamp == 0, "Account already claimed");
        require(userTokenClaims[user].claimedTimestamp == 0, "User already claimed");
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(block.timestamp >= season.startTimestamp, "Season not started");
            require(block.timestamp < season.endTimestamp, "Season already ended");
            require(amount <= season.maxUserClaim, "Invalid user claim amount");
            require(amount.add(season.claimedAmount) <= season.maxTotalClaim, "Season max token claims");
            require(amount.add(season.mintedAmount) <= season.maxTotalMint, "Season max token mints");
        }

        // Register current token claim
        TokenClaim memory claim = TokenClaim({
            account: account,
            user: user,
            claimedAmount: amount,
            claimedTimestamp: block.timestamp
        });
        accountTokenClaims[account] = claim;
        userTokenClaims[user] = claim;
        season.claimedAmount = season.claimedAmount.add(amount);
        season.mintedAmount = season.mintedAmount.add(amount);

        // Mint tokens to received account
        IPrecogToken(token).mint(account, amount);

        emit TokensClaimed(account, user, amount, block.timestamp);
        return true;
    }

    function createMarket(
        string memory name,
        string memory description,
        string memory category,
        string[] memory outcomes,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address creator,
        uint256 funding,
        uint256 overround
    ) external onlyCaller returns (uint256 newMarketId) {
        // Mint tokens to seed the new market
        IPrecogToken(token).mint(address(this), funding);

        // Pack all received information (with defaults), create new market and return new market id
        MarketInfo memory marketInfo = MarketInfo(
            name, description, category, arrayToCSV(outcomes), startTimestamp, endTimestamp, creator, address(0)
        );
        MarketConfig memory marketConfig = MarketConfig(
            outcomes.length, // Total number of outcomes
            funding,         // Initial supply of the market
            overround,       // AMM profit margin
            token,           // collateralToken: Precog Token
            address(this),   // collateralFunder: This contract
            oracle           // marketOracle: Preset oracle
        );
        return _createMarket(marketInfo, marketConfig);
    }

    function createCustomMarket(
        string memory name,
        string memory description,
        string memory category,
        string[] memory outcomes,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address creator,
        uint256 funding,
        uint256 overround,
        address collateralToken,
        address collateralFunder,
        address marketOracle
    ) public onlyMarketCreator returns (uint256 newMarketId) {
        // Pack all received information, create new market and return new market id
        MarketInfo memory marketInfo = MarketInfo(
            name, description, category, arrayToCSV(outcomes), startTimestamp, endTimestamp, creator, address(0)
        );
        MarketConfig memory marketConfig = MarketConfig(
            outcomes.length, funding, overround, collateralToken, collateralFunder, marketOracle
        );
        return _createMarket(marketInfo, marketConfig);
    }

    function _createMarket(MarketInfo memory info, MarketConfig memory config) internal returns (uint256 newMarketId) {
        // Deploy a new market contract and initialize it with the collateral token
        address newMarketAddress = Clones.clone(market);
        IPrecogMarket newMarket = IPrecogMarket(newMarketAddress);
        newMarket.initialize(config.collateralToken);

        // Get funding amount to seed the new market (optimization: except in a self-funder usecase)
        if (config.collateralFunder != address(this)) {
            IERC20(config.collateralToken).safeTransferFrom(config.collateralFunder, address(this), config.funding);
        }
        // Pre approve all trades with
        IERC20(config.collateralToken).approve(newMarketAddress, type(uint256).max);

        // Get new Id and Setup deployed new market
        newMarketId = createdMarkets;
        newMarket.setup(newMarketId, config.marketOracle, config.totalOutcomes, config.funding, config.overround);
        newMarket.updateDates(info.startTimestamp, info.endTimestamp);

        // Save created market address in received info and save it in local storage (indexed by id)
        info.market = newMarketAddress;
        markets[newMarketId] = info;

        // Increase created markets counter and return new market id
        createdMarkets = createdMarkets.add(1);

        emit MarketCreated(info.creator, newMarketId, newMarketAddress);
        return newMarketId;
    }

    function arrayToCSV(string[] memory array) internal pure returns (string memory) {
        bytes memory csvBytes;
        for (uint i = 0; i < array.length; i++) {
            csvBytes = abi.encodePacked(csvBytes, array[i]);
            if (i < array.length - 1) {
                csvBytes = abi.encodePacked(csvBytes, ",");
            }
        }
        return string(csvBytes);
    }

    // Whitelisted functions: Only admin
    function addMarketCreator(address account) external onlyAdmin {
        grantRole(MARKET_CREATOR_ROLE, account);
    }

    function removeMarketCreator(address account) external onlyAdmin {
        revokeRole(MARKET_CREATOR_ROLE, account);
    }

    function addCaller(address account) external onlyAdmin {
        grantRole(CALLER_ROLE, account);
    }

    function removeCaller(address account) external onlyAdmin {
        revokeRole(CALLER_ROLE, account);
    }

    function addAdmin(address account) external onlyAdmin {
        grantRole(ADMIN_ROLE, account);
    }

    function removeAdmin(address account) external onlyAdmin {
        revokeRole(ADMIN_ROLE, account);
    }

    function updateCurrentSeason(uint256 seasonIndex) external onlyAdmin {
        currentSeason = seasonIndex;
    }

    function setMiningSeason(
        uint256 seasonIndex,
        uint256 startTimestamp,
        uint256 endTimestamp,
        uint256 maxUserClaim,
        uint256 maxTotalClaim,
        uint256 maxTotalMint,
        uint256 claimedAmount,
        uint256 mintedAmount
    ) external onlyAdmin {
        miningSeasons[seasonIndex] = MiningSeason({
            startTimestamp: startTimestamp,
            endTimestamp: endTimestamp,
            maxUserClaim: maxUserClaim,
            maxTotalClaim: maxTotalClaim,
            maxTotalMint: maxTotalMint,
            claimedAmount: claimedAmount,
            mintedAmount: mintedAmount
        });
    }

    function setBaseOracle(address _oracle) external onlyAdmin {
        oracle = _oracle;
    }

    function setBaseMarket(address _market) external onlyAdmin {
        market = _market;
    }

    function updateMarket(
        uint256 id,
        string memory name,
        string memory description,
        string memory category,
        string[] memory outcomes,
        uint256 startTimestamp,
        uint256 endTimestamp,
        address marketCreator,
        address marketOracle
    ) external onlyAdmin {
        if (bytes(name).length > 0) {
            markets[id].name = name;
        }
        if (bytes(description).length > 0) {
            markets[id].description = description;
        }
        if (bytes(category).length > 0) {
            markets[id].category = category;
        }
        if (outcomes.length > 0) {
            // Only updates outcome labels (not total possible outcomes)
            markets[id].outcomes = arrayToCSV(outcomes);
        }
        if (marketCreator != address(0)) {
            markets[id].creator = marketCreator;
        }
        if (marketOracle != address(0)) {
            IPrecogMarket(markets[id].market).updateOracle(marketOracle);
        }
        bool updateDates = false;
        if (startTimestamp > 0) {
            markets[id].startTimestamp = startTimestamp;
            updateDates = true;
        }
        if (endTimestamp > 0) {
            markets[id].endTimestamp = endTimestamp;
            updateDates = true;
        }
        if (updateDates) {
            IPrecogMarket(markets[id].market).updateDates(markets[id].startTimestamp, markets[id].endTimestamp);
        }
    }

    function marketWithdraw(uint256 marketId, address marketToken) external onlyAdmin {
        IPrecogMarket(markets[marketId].market).withdraw(marketToken);
    }

    function marketTransferOwnership(uint256 marketId, address newOwner) external onlyAdmin {
        IPrecogMarket(markets[marketId].market).transferOwnership(newOwner);
    }

    function updateTokenClaim(address account, uint256 amount, uint256 timestamp) external onlyAdmin {
        // Used to reset token claims
        accountTokenClaims[account].claimedAmount = amount;
        accountTokenClaims[account].claimedTimestamp = timestamp;
        uint256 user = accountTokenClaims[account].user;
        userTokenClaims[user].claimedAmount = amount;
        userTokenClaims[user].claimedTimestamp = timestamp;
    }

    function precogMint(address to, uint256 amount) external onlyAdmin {
        // Validate token mint
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(amount.add(season.mintedAmount) <= season.maxTotalMint, "Season max token mints");
        }

        // Register token mint
        season.mintedAmount = season.mintedAmount.add(amount);

        // Mint tokens to received account
        IPrecogToken(token).mint(to, amount);

        emit TokensMinted(to, amount, block.timestamp);
    }

    function precogBurn(address from, uint256 amount) external onlyAdmin {
        IPrecogToken(token).burn(from, amount);
    }

    function precogTransferOwnership(address newPrecogMaster) external onlyAdmin {
        IPrecogToken(token).transferOwnership(newPrecogMaster);
    }

    function withdraw(address _token) public onlyAdmin {
        if (_token == address(0)) {
            payable(msg.sender).transfer(address(this).balance);
        } else {
            IERC20(_token).safeTransfer(msg.sender, IERC20(_token).balanceOf(address(this)));
        }
    }
}

